package auth

import (
	"context"
	"encoding/json"
	"errors"
	"fmt"
	"github.com/SENERGY-Platform/mqtt-platform-connector/lib"
	"github.com/SENERGY-Platform/platform-connector-lib/security"
	"github.com/julienschmidt/httprouter"
	"log"
	"net/http"
	"net/http/httptest"
	"runtime/debug"
	"time"
)

func Mock(config *lib.Config, ctx context.Context) (err error) {
	router, err := getRouter()
	if err != nil {
		return err
	}
	server := httptest.NewServer(router)
	config.AuthEndpoint = server.URL
	go func() {
		<-ctx.Done()
		server.Close()
	}()
	return nil
}

func getRouter() (router *httprouter.Router, err error) {
	defer func() {
		if r := recover(); r != nil && err == nil {
			log.Printf("%s: %s", r, debug.Stack())
			err = errors.New(fmt.Sprint("Recovered Error: ", r))
		}
	}()
	router = httprouter.New()

	router.POST("/auth/realms/master/protocol/openid-connect/token", func(writer http.ResponseWriter, request *http.Request, params httprouter.Params) {
		writer.Header().Set("Content-Type", "application/json; charset=utf-8")
		err = json.NewEncoder(writer).Encode(security.OpenidToken{
			AccessToken:      "eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICIzaUtabW9aUHpsMmRtQnBJdS1vSkY4ZVVUZHh4OUFIckVOcG5CcHM5SjYwIn0..eOwKV7vwRrWr8GlfCPFSq5WwR_p-_rSJURXCV1K7ClBY5jqKQkCsRL2V4YhkP1uS6ECeSxF7NNOLmElVLeFyAkvgSNOUkiuIWQpMTakNKynyRfH0SrdnPSTwK2V1s1i4VjoYdyZWXKNjeT2tUUX9eCyI5qOf_Dzcai5FhGCSUeKpV0ScUj5lKrn56aamlW9IdmbFJ4VwpQg2Y843Vc0TqpjK9n_uKwuRcQd9jkKHkbwWQ-wyJEbFWXHjQ6LnM84H0CQ2fgBqPPfpQDKjGSUNaCS-jtBcbsBAWQSICwol95BuOAqVFMucx56Wm-OyQOuoQ1jaLt2t-Uxtr-C9wKJWHQ",
			ExpiresIn:        10 * float64(time.Hour),
			RefreshExpiresIn: 5 * float64(time.Hour),
			RefreshToken:     "",
			TokenType:        "",
			RequestTime:      time.Now(),
		})
		if err != nil {
			log.Println("ERROR: unable to encode response", err)
		}
	})

	router.GET("/auth/admin/realms/master/users", func(writer http.ResponseWriter, request *http.Request, params httprouter.Params) {
		username := request.URL.Query().Get("username")
		writer.Header().Set("Content-Type", "application/json; charset=utf-8")
		err = json.NewEncoder(writer).Encode([]security.UserRepresentation{{
			Id:   username,
			Name: username,
		}})
		if err != nil {
			log.Println("ERROR: unable to encode response", err)
		}
	})

	router.GET("/auth/admin/realms/master/users/:userid/role-mappings/realm", func(writer http.ResponseWriter, request *http.Request, params httprouter.Params) {
		writer.Header().Set("Content-Type", "application/json; charset=utf-8")
		err = json.NewEncoder(writer).Encode([]security.RoleMapping{{Name: "test-group"}})
		if err != nil {
			log.Println("ERROR: unable to encode response", err)
		}
	})

	return
}
